var pstack = require("./pstack.js");

var stack	= new pstack({
	progress:	"test..."
});
var buffer	= {};

var i;
for (i=1;i<30;i++) {
	stack.add(function(done, p) {
		//console.log("["+p.i+"] Reply in: ", p.i*2000);
		
		setTimeout(function() {
			//console.log("["+p.i+"] REPLIED");
			done();
		}, p.i*500);
		
	}, {i:i}, 2);
};

stack.start(function() {
	
});

